<?php

namespace Database\Seeders;

use App\Models\Lesson;
use App\Models\LessonContent;
use App\Models\Subject;
use Illuminate\Database\Seeder;

class LessonContentSeeder extends Seeder
{
    public function run(): void
    {
        // Grade 6 specific content (full from frontend)
        $this->seedForSubject('p6-arabic', 'lesson-1', $this->getP6ArabicContent());
        $this->seedForSubject('p6-science', 'lesson-1', $this->getP6ScienceContent());
        $this->seedForSubject('p6-english', 'lesson-1', $this->getP6EnglishContent());
        $this->seedForSubject('p6-social', 'lesson-1', $this->getP6SocialContent());

        // Generic content: apply to all matching subjects
        $this->seedForSubjectPattern('-math', 'lesson-1', $this->getMathematics1Content());
        $this->seedForSubjectPattern('-math', 'lesson-4', $this->getMathematics4Content());
        $this->seedForSubjectPattern('-arabic', 'lesson-1', $this->getArabic1Content());
        $this->seedForSubjectPattern('-science', 'lesson-1', $this->getScience1Content());
    }

    protected function findLessonBySubjectAndCode(string $subjectCode, string $lessonCode): ?Lesson
    {
        $subject = Subject::where('code', $subjectCode)->first();
        if (!$subject) {
            return null;
        }
        $unitIds = $subject->units()->pluck('id');
        return Lesson::whereIn('unit_id', $unitIds)->where('code', $lessonCode)->first();
    }

    protected function seedForSubject(string $subjectCode, string $lessonCode, array $data): void
    {
        $lesson = $this->findLessonBySubjectAndCode($subjectCode, $lessonCode);
        if ($lesson) {
            LessonContent::updateOrCreate(
                ['lesson_id' => $lesson->id],
                array_merge($data, ['lesson_id' => $lesson->id])
            );
        }
    }

    protected function seedForSubjectPattern(string $suffix, string $lessonCode, array $data): void
    {
        $subjects = Subject::where('code', 'like', '%' . $suffix)->get();
        foreach ($subjects as $subject) {
            $lesson = $this->findLessonBySubjectAndCode($subject->code, $lessonCode);
            if ($lesson) {
                LessonContent::updateOrCreate(
                    ['lesson_id' => $lesson->id],
                    array_merge($data, ['lesson_id' => $lesson->id])
                );
            }
        }
    }

